#!/bin/bashpushd $(dirname "$0") > /dev/nullcd ../..export adirExamples=$(pwd)popd > /dev/nullecho "in run consumer"export LOG=/dev/nullif [ ! -e "$HOME/.uwbd2/config" ]then	echo "could not find required configuration at: $HOME/.uwbd2/config" 1>&2	exit 1fi. "$HOME/.uwbd2/config"if ! docker ps &> /dev/nullthen	echo "Could not find the docker engine.  Did you forget to run this?" 1>&2	echo "" 1>&2	echo "    eval \$(docker-machine env $vmUwbd)" 1>&2	exit 1fiif [ -z "$DOCKER_IP" ]then	export DOCKER_IP=$(docker-machine ip $vmUwbd)fiecho $DOCKER_IPdocker ps --filter 'label=unittest' | cut -c1-12 | tail -n+2 | xargs -L1 "$adirExamples/assignment3/bin/docker-rm.sh" bash $adirExamples/bats/libexec/bats $adirExamples/rundoc.sh